def morseCodify(text):
  key = {"a":".-", "b":"-...", "c":"-.-.", "d":"-..", "e": ".", "f": "..-.", "g": "--.", "h": "....", "i": "..", "j": ".---", "k": "-.-", "l": ".-..", "m": "--", "n": "-.", "o": "---", "p": ".--.", "q": "--.-", "r": ".-.", "s": "...", "t": "-", "u": "..-", "v": "...-", "w": ".--", "x": "-..-", "y": "-.--", "z": "--..", " ": "/", "1": ".----", "0": "-----", "2": "..---", "3": "...--", "4": "....-", "5": ".....", "6": "-....", "7": "--...", "8": "---..", "9": "----.", ".": ".-.-.-", ",": "--..--", "?": "..--..", "=": "-...-"}
  ret = ""
  for char in list(text):
    if char.isalpha():
      char = char.lower()
    ret += key[char] + " "
  return ret

def morseUncode(text):
  ret = ""
  data = []
  key = {"a":".-", "b":"-...", "c":"-.-.", "d":"-..", "e": ".", "f": "..-.", "g": "--.", "h": "....", "i": "..", "j": ".---", "k": "-.-", "l": ".-..", "m": "--", "n": "-.", "o": "---", "p": ".--.", "q": "--.-", "r": ".-.", "s": "...", "t": "-", "u": "..-", "v": "...-", "w": ".--", "x": "-..-", "y": "-.--", "z": "--..", " ": "/", "1": ".----", "0": "-----", "2": "..---", "3": "...--", "4": "....-", "5": ".....", "6": "-....", "7": "--...", "8": "---..", "9": "----.", ".": ".-.-.-", ",": "--..--", "?": "..--..", "=": "-...-"}
  newkey = dict(map(reversed, key.items()))
  text = list(text)
  text.insert(0, " ")
  word = ""
  for char in text:
    if char != " ":
      word += char
    if char == " ":
      if word != "":
        data.append(word)
      word = ""
  out = ""
  for char in data:
    out += newkey[char]
  return out
    